import{_ as a,o as i,c as l,a2 as n}from"./chunks/framework.DVZE_dpK.js";const S=JSON.parse('{"title":"对 DNS 协议的理解","description":"","frontmatter":{},"headers":[],"relativePath":"interview/sceneQuestion/sceneQuestion-13.md","filePath":"interview/sceneQuestion/sceneQuestion-13.md","lastUpdated":1726797991000}'),o={name:"interview/sceneQuestion/sceneQuestion-13.md"};function t(s,e,r,d,h,c){return i(),l("div",null,e[0]||(e[0]=[n('<h1 id="对-dns-协议的理解" tabindex="-1">对 DNS 协议的理解 <a class="header-anchor" href="#对-dns-协议的理解" aria-label="Permalink to &quot;对 DNS 协议的理解&quot;">​</a></h1><h2 id="dns-协议" tabindex="-1">DNS 协议 <a class="header-anchor" href="#dns-协议" aria-label="Permalink to &quot;DNS 协议&quot;">​</a></h2><p>将域名映射到 ip 上<br></p><p>DNS（Domain Name System，域名系统），是互联网的一项服务。它作为将域名和 IP 地址相互映射的一个分布式数据库，能够使人更方便地访问互联网。DNS 使用 TCP 和 UDP 端口 53。当前，对于每一级域名长度的限制是 63 个字符，域名总长度则不能超过 253 个字符。</p><h2 id="dns-解析过程" tabindex="-1">DNS 解析过程 <a class="header-anchor" href="#dns-解析过程" aria-label="Permalink to &quot;DNS 解析过程&quot;">​</a></h2><p>DNS 解析过程如下：</p><ol><li>用户输入域名</li><li>检查 DNS 缓存</li><li>操作系统 DNS 缓存</li><li>本地域名服务器</li><li>根据本地 DNS 服务器去查找跟 DNS 服务器、顶级域名服务器（TLD）、权威 DNS 服务器</li><li>返回 ip 地址给本地 DNS 服务器，浏览器缓存并向 IP 地址发起请求</li></ol><h2 id="dns-记录类型" tabindex="-1">DNS 记录类型 <a class="header-anchor" href="#dns-记录类型" aria-label="Permalink to &quot;DNS 记录类型&quot;">​</a></h2><ol><li>A 记录：将域名映射到 IPv4 地址</li><li>AAAA 记录：将域名映射到 IPv6 地址</li><li>CNAME 记录：将域名映射到另一个域名</li><li>MX 记录：将域名映射到邮件服务器</li><li>TXT 记录：文本信息储存，用于域名验证</li><li>NS 记录：将域名映射到 DNS 服务器</li><li>SOA 记录：定义 DNS 区域的权威信息</li></ol><h2 id="dns-常见问题" tabindex="-1">DNS 常见问题 <a class="header-anchor" href="#dns-常见问题" aria-label="Permalink to &quot;DNS 常见问题&quot;">​</a></h2><h3 id="dns-解析慢" tabindex="-1">DNS 解析慢 <a class="header-anchor" href="#dns-解析慢" aria-label="Permalink to &quot;DNS 解析慢&quot;">​</a></h3><ol><li>DNS 预解析</li><li>使用 CDN，CDN 节点是用户就近</li><li>减少外部资源请求，将自己域名+oss+cdn</li></ol><h3 id="dns-劫持" tabindex="-1">DNS 劫持 <a class="header-anchor" href="#dns-劫持" aria-label="Permalink to &quot;DNS 劫持&quot;">​</a></h3><ol><li>HTTPS，证书能保证传输安全性</li><li>DNSSEC： DNS 安全扩展，防止 DNS 劫持</li></ol><h2 id="优化" tabindex="-1">优化 <a class="header-anchor" href="#优化" aria-label="Permalink to &quot;优化&quot;">​</a></h2><ol><li>DNS 缓存</li><li>nslookup</li><li>dig</li><li>在线：dns.google.com、dnschecker.org</li></ol>',16)]))}const D=a(o,[["render",t]]);export{S as __pageData,D as default};
